import React from 'react';
import coca from "../img/coca.jpg"
import fanta from "../img/fanta.jpg"
import sprite from "../img/Sprite.png"

const card = () => {
    return (
        <div className="container">
            <div className="d-flex">
                <div className="col-4 borderCard mx-2">
                    <img className="img-fluid tailleImg" src={coca} alt="" />
                    <h2 className="ms-5">{props.nom}</h2>
                    <h2 className="ms-5">`Prix : {props.prix}€`</h2>
                    <h2 className="ms-5 mb-4">``Stock : 5 unités</h2>
                    <button className="ms-5 mb-5 fs-1 modifButton">Acheter</button>
                </div>
            

                
            </div>
        </div>
    );
};


export default card;
